import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const ThreeDText = () => {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const groupRef = useRef(null);
  const animationIdRef = useRef(null);

  useEffect(() => {
    if (!mountRef.current) return;

    // Scene setup
    const scene = new THREE.Scene();
    sceneRef.current = scene;

    // Camera setup
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 200);

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ 
      alpha: true,
      antialias: true,
      powerPreference: "high-performance"
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0x000000, 0);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    rendererRef.current = renderer;

    // Lights
    const ambientLight = new THREE.AmbientLight(0x404040, 0.6);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(1, 1, 1);
    scene.add(directionalLight);

    const pointLight = new THREE.PointLight(0xCC9933, 1, 1000);
    pointLight.position.set(0, 0, 100);
    scene.add(pointLight);

    // Group for text
    const group = new THREE.Group();
    scene.add(group);
    groupRef.current = group;

    // Create 3D text using custom letter shapes
    const text = "Hi! I'm Saaketh";
    const letters = text.split('');
    let currentX = 0;

    letters.forEach((letter, index) => {
      if (letter === ' ') {
        currentX += 20; // Space between words
        return;
      }

      let letterGroup = new THREE.Group();
      let letterGeometry;

      // Create actual letter shapes
      switch (letter.toLowerCase()) {
        case 'h':
          // H shape - two vertical bars connected by horizontal bar
          const hLeft = new THREE.Mesh(
            new THREE.BoxGeometry(8, 24, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          hLeft.position.set(-8, 0, 0);
          letterGroup.add(hLeft);

          const hRight = new THREE.Mesh(
            new THREE.BoxGeometry(8, 24, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          hRight.position.set(8, 0, 0);
          letterGroup.add(hRight);

          const hMiddle = new THREE.Mesh(
            new THREE.BoxGeometry(16, 6, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          hMiddle.position.set(0, 0, 0);
          letterGroup.add(hMiddle);
          break;

        case 'i':
          // I shape - vertical bar with dot on top
          const iBar = new THREE.Mesh(
            new THREE.BoxGeometry(6, 20, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          iBar.position.set(0, 0, 0);
          letterGroup.add(iBar);

          const iDot = new THREE.Mesh(
            new THREE.BoxGeometry(6, 6, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          iDot.position.set(0, 12, 0);
          letterGroup.add(iDot);
          break;

        case '!':
          // Exclamation mark - vertical bar with dot below
          const exBar = new THREE.Mesh(
            new THREE.BoxGeometry(6, 18, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          exBar.position.set(0, 2, 0);
          letterGroup.add(exBar);

          const exDot = new THREE.Mesh(
            new THREE.BoxGeometry(6, 6, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          exDot.position.set(0, -10, 0);
          letterGroup.add(exDot);
          break;

        case "'":
          // Apostrophe - small dot
          letterGeometry = new THREE.BoxGeometry(4, 6, 8);
          break;

        case 'm':
          // M shape - three vertical bars with diagonal connections
          const mLeft = new THREE.Mesh(
            new THREE.BoxGeometry(6, 20, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          mLeft.position.set(-12, 0, 0);
          letterGroup.add(mLeft);

          const mRight = new THREE.Mesh(
            new THREE.BoxGeometry(6, 20, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          mRight.position.set(12, 0, 0);
          letterGroup.add(mRight);

          const mMiddle = new THREE.Mesh(
            new THREE.BoxGeometry(6, 20, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          mMiddle.position.set(0, 0, 0);
          letterGroup.add(mMiddle);

          // Diagonal connections
          const mDiag1 = new THREE.Mesh(
            new THREE.BoxGeometry(12, 4, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          mDiag1.position.set(-6, 6, 0);
          mDiag1.rotation.z = Math.PI / 6;
          letterGroup.add(mDiag1);

          const mDiag2 = new THREE.Mesh(
            new THREE.BoxGeometry(12, 4, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          mDiag2.position.set(6, 6, 0);
          mDiag2.rotation.z = -Math.PI / 6;
          letterGroup.add(mDiag2);
          break;

        case 's':
          // S shape - curved using multiple segments
          for (let i = 0; i < 5; i++) {
            const sSegment = new THREE.Mesh(
              new THREE.BoxGeometry(4, 4, 8),
              new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
            );
            sSegment.position.set(
              Math.sin(i * Math.PI / 2) * 8,
              Math.cos(i * Math.PI / 2) * 6 - 6,
              0
            );
            letterGroup.add(sSegment);
          }
          break;

        case 'a':
          // A shape - two diagonal bars with horizontal crossbar
          const aLeft = new THREE.Mesh(
            new THREE.BoxGeometry(4, 20, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          aLeft.position.set(-6, 0, 0);
          aLeft.rotation.z = Math.PI / 6;
          letterGroup.add(aLeft);

          const aRight = new THREE.Mesh(
            new THREE.BoxGeometry(4, 20, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          aRight.position.set(6, 0, 0);
          aRight.rotation.z = -Math.PI / 6;
          letterGroup.add(aRight);

          const aCross = new THREE.Mesh(
            new THREE.BoxGeometry(12, 4, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          aCross.position.set(0, 0, 0);
          letterGroup.add(aCross);
          break;

        case 'k':
          // K shape - vertical bar with two diagonal branches
          const kBar = new THREE.Mesh(
            new THREE.BoxGeometry(6, 20, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          kBar.position.set(-3, 0, 0);
          letterGroup.add(kBar);

          const kTop = new THREE.Mesh(
            new THREE.BoxGeometry(12, 4, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          kTop.position.set(3, 6, 0);
          kTop.rotation.z = -Math.PI / 4;
          letterGroup.add(kTop);

          const kBottom = new THREE.Mesh(
            new THREE.BoxGeometry(12, 4, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          kBottom.position.set(3, -6, 0);
          kBottom.rotation.z = Math.PI / 4;
          letterGroup.add(kBottom);
          break;

        case 'e':
          // E shape - vertical bar with three horizontal bars
          const eBar = new THREE.Mesh(
            new THREE.BoxGeometry(6, 20, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          eBar.position.set(-3, 0, 0);
          letterGroup.add(eBar);

          const eTop = new THREE.Mesh(
            new THREE.BoxGeometry(12, 4, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          eTop.position.set(3, 8, 0);
          letterGroup.add(eTop);

          const eMiddle = new THREE.Mesh(
            new THREE.BoxGeometry(12, 4, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          eMiddle.position.set(3, 0, 0);
          letterGroup.add(eMiddle);

          const eBottom = new THREE.Mesh(
            new THREE.BoxGeometry(12, 4, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          eBottom.position.set(3, -8, 0);
          letterGroup.add(eBottom);
          break;

        case 't':
          // T shape - horizontal bar with vertical bar
          const tTop = new THREE.Mesh(
            new THREE.BoxGeometry(16, 4, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          tTop.position.set(0, 8, 0);
          letterGroup.add(tTop);

          const tBar = new THREE.Mesh(
            new THREE.BoxGeometry(4, 16, 8),
            new THREE.MeshPhongMaterial({ color: 0xCC9933, transparent: true, opacity: 0.9 })
          );
          tBar.position.set(0, 0, 0);
          letterGroup.add(tBar);
          break;

        default:
          // Default letter - simple rectangle
          letterGeometry = new THREE.BoxGeometry(12, 20, 8);
      }

      // If we have a simple geometry, create the mesh
      if (letterGeometry) {
        const material = new THREE.MeshPhongMaterial({
          color: 0xCC9933,
          transparent: true,
          opacity: 0.9,
          shininess: 100
        });
        const letterMesh = new THREE.Mesh(letterGeometry, material);
        letterGroup.add(letterMesh);
      }

      // Position the letter group
      letterGroup.position.x = currentX;
      letterGroup.position.y = 0;
      letterGroup.position.z = 0;

      group.add(letterGroup);

      // Update position for next letter
      if (letter === '!') {
        currentX += 20;
      } else if (letter === "'") {
        currentX += 12;
      } else if (letter === 'h' || letter === 'm') {
        currentX += 30;
      } else if (letter === 's' || letter === 'a' || letter === 'k') {
        currentX += 25;
      } else if (letter === 'e' || letter === 't') {
        currentX += 22;
      } else {
        currentX += 18;
      }
    });

    // Center the entire text group
    group.position.x = -currentX / 2;

    // Add to DOM
    mountRef.current.appendChild(renderer.domElement);

    // Animation loop - no movement, just render
    const animate = () => {
      animationIdRef.current = requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };

    animate();

    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      const currentMount = mountRef.current;
      window.removeEventListener('resize', handleResize);
      if (animationIdRef.current) {
        cancelAnimationFrame(animationIdRef.current);
      }
      if (currentMount && renderer.domElement) {
        currentMount.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  return (
    <div 
      ref={mountRef} 
      style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: '100%',
        height: '100%',
        zIndex: 1,
        pointerEvents: 'none'
      }}
    />
  );
};

export default ThreeDText;
