import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const ParticleBackground = () => {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const shapesRef = useRef(null);
  const animationIdRef = useRef(null);

  useEffect(() => {
    if (!mountRef.current) return;

    // Scene setup
    const scene = new THREE.Scene();
    sceneRef.current = scene;

    // Camera setup
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.set(0, 0, 15);

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ 
      alpha: true,
      antialias: true,
      powerPreference: "high-performance"
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0x000000, 0);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    rendererRef.current = renderer;

    // Create subtle, low-contrast abstract shapes
    const shapes = [];

    // Create organic, fluid blob shapes
    const blobCount = 12;
    for (let i = 0; i < blobCount; i++) {
      const blobGeometry = new THREE.SphereGeometry(
        1 + Math.random() * 2, // Random size
        8 + Math.floor(Math.random() * 8), // Low polygon count for subtlety
        6 + Math.floor(Math.random() * 6)
      );

      // Deform the sphere to create organic shapes
      const positions = blobGeometry.attributes.position.array;
      for (let j = 0; j < positions.length; j += 3) {
        const x = positions[j];
        const y = positions[j + 1];
        const z = positions[j + 2];

        // Add subtle organic deformation
        const noise = Math.sin(x * 2 + i) * Math.cos(y * 2 + i) * Math.sin(z * 2 + i) * 0.3;
        positions[j] += noise;
        positions[j + 1] += noise;
        positions[j + 2] += noise;
      }

      blobGeometry.computeVertexNormals();

      // Very subtle, low-contrast material
      const material = new THREE.MeshBasicMaterial({
        color: 0x1a1a1a, // Very dark grey
        transparent: true,
        opacity: 0.15 + Math.random() * 0.1, // Very low opacity
        side: THREE.DoubleSide
      });

      const blob = new THREE.Mesh(blobGeometry, material);
      blob.position.set(
        (Math.random() - 0.5) * 40,
        (Math.random() - 0.5) * 30,
        (Math.random() - 0.5) * 20
      );
      blob.rotation.set(
        Math.random() * Math.PI,
        Math.random() * Math.PI,
        Math.random() * Math.PI
      );

      scene.add(blob);
      shapes.push(blob);
    }

    // Create subtle outline shapes
    const outlineCount = 8;
    for (let i = 0; i < outlineCount; i++) {
      const outlineGeometry = new THREE.RingGeometry(
        1 + Math.random() * 3,
        2 + Math.random() * 4,
        16 + Math.floor(Math.random() * 16)
      );

      const outlineMaterial = new THREE.MeshBasicMaterial({
        color: 0x2a2a2a, // Slightly lighter dark grey
        transparent: true,
        opacity: 0.08 + Math.random() * 0.07, // Very low opacity
        side: THREE.DoubleSide,
        wireframe: false
      });

      const outline = new THREE.Mesh(outlineGeometry, outlineMaterial);
      outline.position.set(
        (Math.random() - 0.5) * 35,
        (Math.random() - 0.5) * 25,
        (Math.random() - 0.5) * 15
      );
      outline.rotation.x = Math.PI / 2;

      scene.add(outline);
      shapes.push(outline);
    }

    // Create some irregular disc shapes with inner rings
    const discCount = 6;
    for (let i = 0; i < discCount; i++) {
      // Outer disc
      const outerGeometry = new THREE.CircleGeometry(2 + Math.random() * 3, 32);
      const outerMaterial = new THREE.MeshBasicMaterial({
        color: 0x1f1f1f, // Very dark grey
        transparent: true,
        opacity: 0.12 + Math.random() * 0.08,
        side: THREE.DoubleSide
      });

      const outerDisc = new THREE.Mesh(outerGeometry, outerMaterial);
      outerDisc.position.set(
        (Math.random() - 0.5) * 30,
        (Math.random() - 0.5) * 20,
        (Math.random() - 0.5) * 10
      );
      outerDisc.rotation.x = Math.PI / 2;

      scene.add(outerDisc);
      shapes.push(outerDisc);

      // Inner ring
      const innerGeometry = new THREE.RingGeometry(0.5, 1 + Math.random() * 1.5, 24);
      const innerMaterial = new THREE.MeshBasicMaterial({
        color: 0x333333, // Slightly lighter grey for inner ring
        transparent: true,
        opacity: 0.1 + Math.random() * 0.05,
        side: THREE.DoubleSide
      });

      const innerRing = new THREE.Mesh(innerGeometry, innerMaterial);
      innerRing.position.copy(outerDisc.position);
      innerRing.rotation.copy(outerDisc.rotation);

      scene.add(innerRing);
      shapes.push(innerRing);
    }

    shapesRef.current = shapes;

    // Add to DOM
    mountRef.current.appendChild(renderer.domElement);

    // Animation loop - very subtle movement
    const animate = () => {
      animationIdRef.current = requestAnimationFrame(animate);

      if (shapesRef.current) {
        // Very subtle, slow movement
        shapesRef.current.forEach((shape, index) => {
          // Extremely slow rotation
          shape.rotation.x += 0.0002 + Math.random() * 0.0003;
          shape.rotation.y += 0.0003 + Math.random() * 0.0002;
          shape.rotation.z += 0.0001 + Math.random() * 0.0002;

          // Very subtle position drift
          shape.position.x += Math.sin(Date.now() * 0.0001 + index) * 0.002;
          shape.position.y += Math.cos(Date.now() * 0.0001 + index) * 0.0015;
          shape.position.z += Math.sin(Date.now() * 0.0001 + index * 0.5) * 0.001;

          // Keep shapes within bounds
          if (shape.position.x > 25) shape.position.x = -25;
          if (shape.position.x < -25) shape.position.x = 25;
          if (shape.position.y > 20) shape.position.y = -20;
          if (shape.position.y < -20) shape.position.y = 20;
          if (shape.position.z > 15) shape.position.z = -15;
          if (shape.position.z < -15) shape.position.z = 15;
        });
      }

      // Extremely subtle camera movement
      camera.position.x = Math.sin(Date.now() * 0.00001) * 0.2;
      camera.position.y = Math.cos(Date.now() * 0.000008) * 0.1;

      renderer.render(scene, camera);
    };

    animate();

    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      const currentMount = mountRef.current;
      window.removeEventListener('resize', handleResize);
      if (animationIdRef.current) {
        cancelAnimationFrame(animationIdRef.current);
      }
      if (currentMount && renderer.domElement) {
        currentMount.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  return (
    <div 
      ref={mountRef} 
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: -1,
        pointerEvents: 'none'
      }}
    />
  );
};

export default ParticleBackground;
