import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const ThreeDText = () => {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const groupRef = useRef(null);
  const animationIdRef = useRef(null);

  useEffect(() => {
    if (!mountRef.current) return;

    // Scene setup
    const scene = new THREE.Scene();
    sceneRef.current = scene;

    // Camera setup
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 200);

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ 
      alpha: true,
      antialias: true,
      powerPreference: "high-performance"
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0x000000, 0);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    rendererRef.current = renderer;

    // Lights
    const ambientLight = new THREE.AmbientLight(0x404040, 0.6);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(1, 1, 1);
    scene.add(directionalLight);

    const pointLight = new THREE.PointLight(0xCC9933, 1, 1000);
    pointLight.position.set(0, 0, 100);
    scene.add(pointLight);

    // Group for text
    const group = new THREE.Group();
    scene.add(group);
    groupRef.current = group;

    // Create 3D text using basic shapes
    const text = "Hi! I'm Saaketh";
    const letters = text.split('');
    let currentX = 0;

    letters.forEach((letter, index) => {
      if (letter === ' ') {
        currentX += 20; // Space between words
        return;
      }

      // Create a 3D letter using basic geometry
      let geometry;

      if (letter === '!') {
        // Exclamation mark - vertical rectangle
        geometry = new THREE.BoxGeometry(8, 20, 8);
      } else if (letter === "'") {
        // Apostrophe - small cube
        geometry = new THREE.BoxGeometry(6, 8, 6);
      } else {
        // Regular letters - rectangular prism
        geometry = new THREE.BoxGeometry(12, 20, 8);
      }

      // Create material with gold color
      const material = new THREE.MeshPhongMaterial({
        color: 0xCC9933, // Muted gold
        transparent: true,
        opacity: 0.9,
        shininess: 100
      });

      const letterMesh = new THREE.Mesh(geometry, material);

      // Position letters
      letterMesh.position.x = currentX;
      letterMesh.position.y = 0;
      letterMesh.position.z = 0;

      // No random rotation - letters stay perfectly straight
      letterMesh.rotation.x = 0;
      letterMesh.rotation.y = 0;
      letterMesh.rotation.z = 0;

      group.add(letterMesh);

      // Update position for next letter
      if (letter === '!') {
        currentX += 15;
      } else if (letter === "'") {
        currentX += 12;
      } else {
        currentX += 18;
      }
    });

    // Center the entire text group
    group.position.x = -currentX / 2;

    // Add to DOM
    mountRef.current.appendChild(renderer.domElement);

    // Animation loop
    const animate = () => {
      animationIdRef.current = requestAnimationFrame(animate);

      // No movement - just render the static scene
      renderer.render(scene, camera);
    };

    animate();

    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      const currentMount = mountRef.current;
      window.removeEventListener('resize', handleResize);
      if (animationIdRef.current) {
        cancelAnimationFrame(animationIdRef.current);
      }
      if (currentMount && renderer.domElement) {
        currentMount.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  return (
    <div 
      ref={mountRef} 
      style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: '100%',
        height: '100%',
        zIndex: 1,
        pointerEvents: 'none'
      }}
    />
  );
};

export default ThreeDText;
