import React, { useState, useEffect } from "react";
import "./App.css";
import Card from "./Card.js";
import ParticleBackground from "./ParticleBackground.js";

function App() {
  const [scrollProgress, setScrollProgress] = useState(0);
  const [activeSection, setActiveSection] = useState('Introduction');

  useEffect(() => {
    const handleScroll = () => {
      const totalHeight = document.documentElement.scrollHeight - window.innerHeight;
      const currentProgress = (window.scrollY / totalHeight) * 100;
      setScrollProgress(currentProgress);

      // Determine active section
      const sections = ['Introduction', 'Projects', 'Contact'];
      const scrollPosition = window.scrollY + 100; // Offset for better detection

      for (let i = sections.length - 1; i >= 0; i--) {
        const section = document.getElementById(sections[i]);
        if (section && section.offsetTop <= scrollPosition) {
          setActiveSection(sections[i]);
          break;
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const smoothScrollTo = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({
        behavior: 'smooth',
        block: 'start'
      });
    }
  };

  const getIndicatorPosition = () => {
    const navButtons = document.querySelectorAll('.nav_place li a');
    let left = 0;
    for (let i = 0; i < navButtons.length; i++) {
      if (navButtons[i].textContent === activeSection) {
        left = navButtons[i].offsetLeft;
        break;
      }
    }
    return `${left}px`;
  };

  return (
    <div className="App">
      <ParticleBackground />
      <div
        style={{
          display: "flex", // Keeps the length constant
          flexDirection: "column", // Keeps the page width constant
          alignItems: "stretch", // Keeps the page width constant
        }}
      >
        <aside
          style={{
            position: "fixed",
            height: "50px",
            width: "100%",
          }}
        >
          {/* Scroll Progress Bar */}
          <div className="scroll-progress-container">
            <div 
              className="scroll-progress-bar" 
              style={{ width: `${scrollProgress}%` }}
            ></div>
          </div>

          {/* Breadcrumb Navigation */}
          <div className="breadcrumb-container">
            <span 
              className="breadcrumb-item" 
              onClick={() => smoothScrollTo('Introduction')}
            >
              Home
            </span>
            <span className="breadcrumb-separator">/</span>
            <span className="breadcrumb-item breadcrumb-active">{activeSection}</span>
          </div>

          <nav>
            <div className="nav-indicator" style={{ left: getIndicatorPosition() }}></div>
            <ul className="nav_place">
              <li>
                <a 
                  className={`nav_buttons ${activeSection === 'Introduction' ? 'nav_active' : ''}`} 
                  href="#Introduction"
                  onClick={(e) => {
                    e.preventDefault();
                    smoothScrollTo('Introduction');
                  }}
                >
                  Introduction
                </a>
              </li>
              <li>
                <a 
                  className={`nav_buttons ${activeSection === 'Projects' ? 'nav_active' : ''}`} 
                  href="#Projects"
                  onClick={(e) => {
                    e.preventDefault();
                    smoothScrollTo('Projects');
                  }}
                >
                  Projects
                </a>
              </li>
              <li>
                <a 
                  className={`nav_buttons ${activeSection === 'Contact' ? 'nav_active' : ''}`} 
                  href="#Contact"
                  onClick={(e) => {
                    e.preventDefault();
                    smoothScrollTo('Contact');
                  }}
                >
                  Contact
                </a>
              </li>
            </ul>
          </nav>
        </aside>

        <main
          style={{
            marginLeft: "0%",
            backgroundColor: "rgba(0, 0, 0, 0.9)", // Black background
          }}
        >
          <header className="App-header">
            <div
              className="bubble-text-container"
              style={{ marginLeft: "-59%" }}
