import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const ParticleBackground = () => {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const particlesRef = useRef(null);
  const terrainRef = useRef(null);
  const animationIdRef = useRef(null);
  const mouseRef = useRef({ x: 0, y: 0 });

  useEffect(() => {
    if (!mountRef.current) return;

    // Scene setup
    const scene = new THREE.Scene();
    sceneRef.current = scene;

    // Camera setup
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.set(0, 0, 15);

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ 
      alpha: true,
      antialias: true,
      powerPreference: "high-performance"
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0x000000, 0);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    rendererRef.current = renderer;

    // Create 3D terrain (landscape)
    const terrainGeometry = new THREE.PlaneGeometry(40, 40, 50, 50);
    const terrainMaterial = new THREE.MeshBasicMaterial({
      color: 0xf6f4f1,
      transparent: true,
      opacity: 0.1,
      wireframe: true
    });

    // Add some terrain variation
    const vertices = terrainGeometry.attributes.position.array;
    for (let i = 0; i < vertices.length; i += 3) {
      const x = vertices[i];
      const z = vertices[i + 2];
      vertices[i + 1] = Math.sin(x * 0.5) * Math.cos(z * 0.5) * 2;
    }

    const terrain = new THREE.Mesh(terrainGeometry, terrainMaterial);
    terrain.rotation.x = -Math.PI / 2;
    terrain.position.y = -5;
    scene.add(terrain);
    terrainRef.current = terrain;

    // Create drifting particles (like pixels from the background)
    const particleCount = 1200;
    const positions = new Float32Array(particleCount * 3);
    const colors = new Float32Array(particleCount * 3);
    const velocities = new Float32Array(particleCount * 3);
    const sizes = new Float32Array(particleCount);

    for (let i = 0; i < particleCount * 3; i += 3) {
      // Spread particles across the landscape
      positions[i] = (Math.random() - 0.5) * 40; // x
      positions[i + 1] = Math.random() * 10 - 2; // y (above and below terrain)
      positions[i + 2] = (Math.random() - 0.5) * 40; // z

      // Random velocities for drifting effect
      velocities[i] = (Math.random() - 0.5) * 0.02; // x drift
      velocities[i + 1] = (Math.random() - 0.5) * 0.01; // y drift
      velocities[i + 2] = (Math.random() - 0.5) * 0.02; // z drift

      // Random sizes for variety
      sizes[i / 3] = Math.random() * 0.05 + 0.01;

      // Colors that match your background theme
      const colorVariations = [
        [0.965, 0.957, 0.945], // #f6f4f1 (your background color)
        [0.545, 0.361, 0.961], // #8b5cf6 (purple accent)
        [0.659, 0.333, 0.969], // #a855f7 (purple accent)
        [0.753, 0.518, 0.988], // #c084fc (purple accent)
        [0.847, 0.694, 0.996], // #d8b4fe (light purple)
        [0.941, 0.871, 1.000], // #f0e7ff (very light purple)
        [0.8, 0.8, 0.8],       // Light gray
        [0.9, 0.9, 0.9]        // Very light gray
      ];
      const colorIndex = Math.floor(Math.random() * colorVariations.length);
      colors[i] = colorVariations[colorIndex][0];
      colors[i + 1] = colorVariations[colorIndex][1];
      colors[i + 2] = colorVariations[colorIndex][2];
    }

    const particleGeometry = new THREE.BufferGeometry();
    particleGeometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));
    particleGeometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));
    particleGeometry.setAttribute('size', new THREE.BufferAttribute(sizes, 1));

    const particleMaterial = new THREE.PointsMaterial({
      size: 1,
      vertexColors: true,
      transparent: true,
      opacity: 0.8,
      sizeAttenuation: true,
      blending: THREE.AdditiveBlending
    });

    const particles = new THREE.Points(particleGeometry, particleMaterial);
    scene.add(particles);
    particlesRef.current = particles;

    // Mouse interaction
    const handleMouseMove = (event) => {
      mouseRef.current.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouseRef.current.y = -(event.clientY / window.innerHeight) * 2 + 1;
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Add to DOM
    mountRef.current.appendChild(renderer.domElement);

    // Animation loop
    const animate = () => {
      animationIdRef.current = requestAnimationFrame(animate);

      if (particlesRef.current) {
        const positions = particlesRef.current.geometry.attributes.position.array;

        // Update particle positions for drifting effect
        for (let i = 0; i < positions.length; i += 3) {
          // Add drifting motion
          positions[i] += velocities[i];
          positions[i + 1] += velocities[i + 2];
          positions[i + 2] += velocities[i + 1];

          // Keep particles within bounds
          if (positions[i] > 20) positions[i] = -20;
          if (positions[i] < -20) positions[i] = 20;
          if (positions[i + 1] > 8) positions[i + 1] = -2;
          if (positions[i + 1] < -2) positions[i + 1] = 8;
          if (positions[i + 2] > 20) positions[i + 2] = -20;
          if (positions[i + 2] < -20) positions[i + 2] = 20;

          // Mouse interaction - particles gently move toward mouse
          const mouseInfluence = 0.005;
          const dx = mouseRef.current.x * 20 - positions[i];
          const dy = mouseRef.current.y * 10 - positions[i + 1];
          const distanceToMouse = Math.sqrt(dx * dx + dy * dy);

          if (distanceToMouse < 8) {
            const force = (8 - distanceToMouse) * mouseInfluence;
            positions[i] += dx * force;
            positions[i + 1] += dy * force;
          }
        }

        particlesRef.current.geometry.attributes.position.needsUpdate = true;

        // Gentle terrain rotation
        if (terrainRef.current) {
          terrainRef.current.rotation.y += 0.001;
        }
      }

      // Subtle camera movement for parallax effect
      camera.position.x = Math.sin(Date.now() * 0.00003) * 1;
      camera.position.y = Math.cos(Date.now() * 0.00002) * 0.5;

      renderer.render(scene, camera);
    };

    animate();

    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      const currentMount = mountRef.current;
      window.removeEventListener('resize', handleResize);
      window.removeEventListener('mousemove', handleMouseMove);
      if (animationIdRef.current) {
        cancelAnimationFrame(animationIdRef.current);
      }
      if (currentMount && renderer.domElement) {
        currentMount.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  return (
    <div 
      ref={mountRef} 
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: -1,
        pointerEvents: 'none'
      }}
    />
  );
};

export default ParticleBackground;
