import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { FontLoader } from 'three/addons/loaders/FontLoader.js';
import { TextGeometry } from 'three/addons/geometries/TextGeometry.js';

const ThreeDText = () => {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const textMeshRef = useRef(null);
  const groupRef = useRef(null);
  const animationIdRef = useRef(null);

  useEffect(() => {
    if (!mountRef.current) return;

    // Scene setup
    const scene = new THREE.Scene();
    sceneRef.current = scene;

    // Camera setup
    const camera = new THREE.PerspectiveCamera(30, window.innerWidth / window.innerHeight, 1, 1500);
    camera.position.set(0, 0, 700);
    camera.lookAt(0, 0, 0);

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ 
      alpha: true,
      antialias: true,
      powerPreference: "high-performance"
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0x000000, 0);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    rendererRef.current = renderer;

    // Lights
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
    dirLight.position.set(0, 0, 1).normalize();
    scene.add(dirLight);

    const pointLight = new THREE.PointLight(0xffffff, 2, 0, 0);
    pointLight.color.setHSL(0.12, 1, 0.7); // Gold color
    pointLight.position.set(0, 100, 90);
    scene.add(pointLight);

    // Materials
    const materials = [
      new THREE.MeshPhongMaterial({ 
        color: 0xCC9933, // Muted gold
        flatShading: true,
        transparent: true,
        opacity: 0.9
      }), // front
      new THREE.MeshPhongMaterial({ 
        color: 0x8B7355, // Darker gold for sides
        transparent: true,
        opacity: 0.8
      }) // side
    ];

    // Group for text
    const group = new THREE.Group();
    group.position.y = 0;
    scene.add(group);
    groupRef.current = group;

    // Text parameters
    const text = "Hi! I'm Saaketh";
    const size = 40;
    const depth = 8;
    const hover = 0;
    const curveSegments = 4;
    const bevelThickness = 1;
    const bevelSize = 0.5;
    const bevelEnabled = true;

    // Load font and create text
    const loader = new FontLoader();
    loader.load('/fonts/optimer_bold.typeface.json', function (font) {
      const textGeo = new TextGeometry(text, {
        font: font,
        size: size,
        depth: depth,
        curveSegments: curveSegments,
        bevelThickness: bevelThickness,
        bevelSize: bevelSize,
        bevelEnabled: bevelEnabled
      });

      textGeo.computeBoundingBox();
      const centerOffset = -0.5 * (textGeo.boundingBox.max.x - textGeo.boundingBox.min.x);

      const textMesh = new THREE.Mesh(textGeo, materials);
      textMesh.position.x = centerOffset;
      textMesh.position.y = hover;
      textMesh.position.z = 0;

      group.add(textMesh);
      textMeshRef.current = textMesh;
    });

    // Add to DOM
    mountRef.current.appendChild(renderer.domElement);

    // Animation loop
    const animate = () => {
      animationIdRef.current = requestAnimationFrame(animate);

      if (groupRef.current) {
        // Gentle rotation for dynamic effect
        groupRef.current.rotation.y += 0.005;
        groupRef.current.rotation.x = Math.sin(Date.now() * 0.001) * 0.1;
      }

      renderer.render(scene, camera);
    };

    animate();

    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      const currentMount = mountRef.current;
      window.removeEventListener('resize', handleResize);
      if (animationIdRef.current) {
        cancelAnimationFrame(animationIdRef.current);
      }
      if (currentMount && renderer.domElement) {
        currentMount.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  return (
    <div 
      ref={mountRef} 
      style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: '100%',
        height: '100%',
        zIndex: 1,
        pointerEvents: 'none'
      }}
    />
  );
};

export default ThreeDText;
