import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const ParticleBackground = () => {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const linesRef = useRef(null);
  const animationIdRef = useRef(null);
  const mouseRef = useRef({ x: 0, y: 0 });

  useEffect(() => {
    if (!mountRef.current) return;

    // Scene setup
    const scene = new THREE.Scene();
    sceneRef.current = scene;

    // Camera setup
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.set(0, 0, 15);

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ 
      alpha: true,
      antialias: true,
      powerPreference: "high-performance"
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0x000000, 0);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    rendererRef.current = renderer;

    // Create flowing, luminous lines
    const lineCount = 50;
    const lines = [];
    const lineGeometries = [];
    const lineMaterials = [];

    for (let i = 0; i < lineCount; i++) {
      // Create curved line geometry
      const points = [];
      const segments = 50 + Math.floor(Math.random() * 30);

      // Generate organic, flowing curve
      let x = (Math.random() - 0.5) * 40;
      let y = (Math.random() - 0.5) * 30;
      let z = (Math.random() - 0.5) * 20;

      for (let j = 0; j < segments; j++) {
        const t = j / (segments - 1);

        // Create organic, swirling motion
        const swirlX = Math.sin(t * Math.PI * 4 + i * 0.5) * 2;
        const swirlY = Math.cos(t * Math.PI * 3 + i * 0.3) * 1.5;
        const swirlZ = Math.sin(t * Math.PI * 2 + i * 0.7) * 1;

        x += (Math.random() - 0.5) * 0.5 + swirlX * 0.1;
        y += (Math.random() - 0.5) * 0.3 + swirlY * 0.1;
        z += (Math.random() - 0.5) * 0.2 + swirlZ * 0.1;

        points.push(new THREE.Vector3(x, y, z));
      }

      const curve = new THREE.CatmullRomCurve3(points);
      const geometry = new THREE.TubeGeometry(curve, segments, 0.05, 8, false);
      lineGeometries.push(geometry);

      // Create luminous material with gold tones
      const material = new THREE.MeshBasicMaterial({
        color: new THREE.Color().setHSL(0.12 + Math.random() * 0.05, 0.8, 0.6 + Math.random() * 0.3), // Gold to amber
        transparent: true,
        opacity: 0.8 + Math.random() * 0.2,
        emissive: new THREE.Color().setHSL(0.12 + Math.random() * 0.05, 0.8, 0.4 + Math.random() * 0.3),
        emissiveIntensity: 0.5 + Math.random() * 0.5
      });

      lineMaterials.push(material);

      const line = new THREE.Mesh(geometry, material);
      scene.add(line);
      lines.push(line);
    }

    linesRef.current = lines;

    // Add some circular vortex patterns
    const vortexCount = 8;
    for (let i = 0; i < vortexCount; i++) {
      const vortexGeometry = new THREE.RingGeometry(0.5, 2 + Math.random() * 3, 32);
      const vortexMaterial = new THREE.MeshBasicMaterial({
        color: new THREE.Color().setHSL(0.12 + Math.random() * 0.05, 0.8, 0.7 + Math.random() * 0.3),
        transparent: true,
        opacity: 0.6 + Math.random() * 0.4,
        side: THREE.DoubleSide
      });

      const vortex = new THREE.Mesh(vortexGeometry, vortexMaterial);
      vortex.position.set(
        (Math.random() - 0.5) * 30,
        (Math.random() - 0.5) * 20,
        (Math.random() - 0.5) * 10
      );
      vortex.rotation.x = Math.PI / 2;
      scene.add(vortex);
      lines.push(vortex);
    }

    // Mouse interaction
    const handleMouseMove = (event) => {
      mouseRef.current.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouseRef.current.y = -(event.clientY / window.innerHeight) * 2 + 1;
    };

    window.addEventListener('mousemove', handleMouseMove);

    // Add to DOM
    mountRef.current.appendChild(renderer.domElement);

    // Animation loop
    const animate = () => {
      animationIdRef.current = requestAnimationFrame(animate);

      if (linesRef.current) {
        // Animate each line with flowing motion
        linesRef.current.forEach((line, index) => {
          // Gentle rotation for organic movement
          line.rotation.x += 0.001 + Math.random() * 0.002;
          line.rotation.y += 0.002 + Math.random() * 0.003;
          line.rotation.z += 0.001 + Math.random() * 0.001;

          // Subtle position drift
          line.position.x += Math.sin(Date.now() * 0.001 + index) * 0.01;
          line.position.y += Math.cos(Date.now() * 0.001 + index) * 0.008;
          line.position.z += Math.sin(Date.now() * 0.001 + index * 0.5) * 0.005;

          // Keep lines within bounds
          if (line.position.x > 25) line.position.x = -25;
          if (line.position.x < -25) line.position.x = 25;
          if (line.position.y > 20) line.position.y = -20;
          if (line.position.y < -20) line.position.y = 20;
          if (line.position.z > 15) line.position.z = -15;
          if (line.position.z < -15) line.position.z = 15;

          // Mouse interaction - lines gently flow toward cursor
          const mouseInfluence = 0.003;
          const dx = mouseRef.current.x * 20 - line.position.x;
          const dy = mouseRef.current.y * 15 - line.position.y;
          const distanceToMouse = Math.sqrt(dx * dx + dy * dy);

          if (distanceToMouse < 8) {
            const force = (8 - distanceToMouse) * mouseInfluence;
            line.position.x += dx * force;
            line.position.y += dy * force;
          }
        });
      }

      // Subtle camera movement for parallax effect
      camera.position.x = Math.sin(Date.now() * 0.00002) * 0.5;
      camera.position.y = Math.cos(Date.now() * 0.000015) * 0.3;

      renderer.render(scene, camera);
    };

    animate();

    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      const currentMount = mountRef.current;
      window.removeEventListener('resize', handleResize);
      window.removeEventListener('mousemove', handleMouseMove);
      if (animationIdRef.current) {
        cancelAnimationFrame(animationIdRef.current);
      }
      if (currentMount && renderer.domElement) {
        currentMount.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  return (
    <div 
      ref={mountRef} 
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: -1,
        pointerEvents: 'none'
      }}
    />
  );
};

export default ParticleBackground;
